const App = () => {
  const [value, setValue] = useState('')
  const [theme, setTheme] = useState('')
  const input = useRef(null)
  const handleChange = async (themes: string) => {
    console.log(input.current?.resizableTextArea?.textArea.value);
    setTheme(themes)
    const mdValue = await md2html(input.current?.resizableTextArea?.textArea.value)
    setValue(themes + mdValue)
    // setValue(theme + value)
  }
  return (
    <div className="editor-area">
      <div>
        <Select value={theme} style={{width: 120}} options={themeList} onChange={handleChange}>
        </Select>
      </div>
      <Row className='editor-content'>
        <Col span={12}>
          <TextArea
            className='m-textarea'
            ref={input}
            onChange={async (e) => {
              let md = await md2html(e.target.value);
              setValue(theme ? theme + md : md)
            }}/>
        </Col>
        <Col span={12}>
          <div dangerouslySetInnerHTML={{__html: value}} id='markdown' className='markdown-body'/>
        </Col>
      </Row>
    </div>
  )
}

export default App;
